SELECT status, target
FROM gbif_doi
WHERE doi = #{doi,jdbcType=OTHER}

SELECT doi, target, status, target
FROM gbif_doi


  status=#{type,jdbcType=OTHER}


  AND type=#{type,jdbcType=OTHER}



  LIMIT #{page.limit} OFFSET #{page.offset}

SELECT metadata
FROM gbif_doi
WHERE doi = #{doi,jdbcType=OTHER}
